    try {
      paginatedResults(users)(req, res, () => res.status(200).json(res.paginatedResults));
      viewAllUsers(req, res.paginatedResults);
    } catch (error) {
      UserError(req, error);
      return res.status(500).json({ error: error.message });
    }
  }
}

